console.log(&quot;foo() --------------------------------------------------&quot;)

var a = 1;
function foo() {
    var a = 2;
    console.log(a);
}
foo();  // 2
console.log(a); // 1 

console.log(&quot;bar() --------------------------------------------------&quot;)

// function block
var b;
function bar() {
    var b = 1;
    if (true) {
      var b = 2;  // same variable!
      console.log(b);  // 2
    }
    console.log(b);  // 2
}
bar();

console.log(&quot;foobar() --------------------------------------------------&quot;)

var c;
function foobar() {
    var c = 1;
    if (true) {
      let c = 2;  // 
      console.log(c);  // 2
    }
    console.log(c);  // 1
}
foobar();
